INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562006, 'Thalidomide', 'Selinexor', 'Moderate', 'Thalidomide can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage.', 'DDInter', 1767317477, 'Caution is advised if thalidomide is used in combination with other neurotoxic agents.  All patients treated with thalidomide should be examined at monthly intervals for the first three months of therapy and periodically thereafter to detect early signs of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.  Electrophysiological testing may be performed at baseline and every six months during therapy to detect asymptomatic neuropathy.  Consideration should be given to immediate discontinuation of thalidomide in patients who develop peripheral neuropathy to limit further damage.  Symptoms may improve or return to baseline in some patients upon discontinuation of thalidomide, although the complete time course of this toxicity has not been fully characterized.  If necessary, therapy should generally be reinstituted only after neuropathy returns to baseline status.  A dosage reduction of thalidomide may be required as clinically indicated.', 'Synergism', 'Caution is advised if thalidomide is used in combination with other neurotoxic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
