INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562020, 'Selpercatinib', 'Avatrombopag', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib.  The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 1767317477, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562021, 'Berotralstat', 'Selpercatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends decreasing the dosage of selpercatinib.  Patients receiving selpercatinib 120 mg twice daily should have the dosage reduced to 80 mg twice daily, and those receiving 160 mg twice daily should have the dosage reduced to 120 mg twice daily.  Close monitoring for adverse effects is advisable, including more frequent ECGs and laboratory monitoring of liver enzymes, bilirubin, electrolytes, glucose, and blood counts.  Selpercatinib treatment should be discontinued, interrupted, or dosage reduced in patients with serious or life-threatening toxicities in accordance with the product labeling.  After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the selpercatinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562022, 'Betrixaban', 'Selpercatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when betrixaban is used with P-gp inhibitors.  Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy.  Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).  Concomitant use of betrixaban with P-gp inhibitors should be avoided in patients with severe renal impairment (CrCl<30 mL/min).', 'Excretion', 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
