INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562037, 'Selpercatinib', 'Lusutrombopag', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', 'DDInter', 1767317477, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.  Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Metabolism', 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562038, 'Selpercatinib', 'Methylnaltrexone', 'Minor', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the renal transporter multidrug and toxin extrusion protein 1 (MATE1).', 'DDInter', 1767317477, 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the renal transporter multidrug and toxin extrusion protein 1 (MATE1).  In vitro studies indicate that selpercatinib inhibits MATE1 at clinically relevant concentrations.  However, the clinical significance of this finding has not been established.  No clinically significant differences in glucose levels were observed when metformin, a MATE1 substrate, was coadministered with selpercatinib.', 'Excretion', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the renal transporter multidrug and toxin extrusion protein 1 (MATE1).', NULL, NULL, 'Minor', 'pharmacodynamic');
