INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562146, 'Simeprevir', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may increase the plasma concentrations of drugs that are P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic anion transporting polypeptide 1B1 (OATP1B1), OATP1B3, organic anion transporter 1 (OAT1), multidrug and toxin extrusion 1 (MATE1), and/or MATE2-K substrates.', 'DDInter', 1767317477, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with substrates of these transporters for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism, Excretion', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
