INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562183, 'Dexmethylphenidate', 'Nitroprusside', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', 'DDInter', 1767317477, 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
