    ) VALUES (1562689, 'Bupropion', 'Arformoterol', 'Moderate', 'Translated with DeepL', NULL, 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.', NULL, 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.  Blood pressure and heart rate should be measured prior to initiating bupropion therapy and monitored at regular intervals consistent with usual clinical practice, particularly in patients with preexisting hypertension.  Dose reduction or discontinuation of bupropion should be considered in patients who experience clinically significant and sustained increases in blood pressure or heart rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
