INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562866, 'Eslicarbazepine', 'Acrivastine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Cautious dosage titration may be required, particularly at treatment initiation.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562867, 'Eslicarbazepine', 'Atenolol', 'Moderate', 'There is clinical evidence that eslicarbazepine acetate can prolong the PR interval of the electrocardiogram (ECG) in some patients.  Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, lopinavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular (AV) block.', 'DDInter', 1767317477, 'Caution is advised if eslicarbazepine is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block; second-degree or higher AV block; sick sinus syndrome without pacemaker), or a history of syncope, arrhythmia or severe cardiac disease such as myocardial ischemia or heart failure.  Patients should be advised to notify their doctor if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if eslicarbazepine is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
