INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562913, 'Eslicarbazepine', 'Ulipristal', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ulipristal acetate and its pharmacologically active metabolite.  Based on in vitro and pharmacokinetic data, ulipristal acetate is thought to be primarily metabolized by CYP450 3A4 to mono-demethylated and di-demethylated metabolites.', 'DDInter', 1767317477, 'When used to prevent pregnancy, ulipristal acetate should not be coadministered with CYP450 3A4 inducers due to the potential for loss of therapeutic efficacy.  For patients who have used enzyme-inducing drugs within the past 4 weeks and are seeking emergency contraception, ulipristal acetate is not recommended and a non-hormonal method (i.e.  a copper intrauterine device (Cu-IUD)) should be considered.', 'Metabolism', 'When used to prevent pregnancy, ulipristal acetate should not be coadministered with CYP450 3A4 inducers due to the potential for loss of therapeutic efficacy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
