INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562955, 'Bromfenac', 'Ardeparin', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs).  The development of epidural and spinal hematoma can lead to long-term or permanent paralysis.', 'DDInter', 1767317477, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.  Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.  In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.  Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', NULL, NULL, 'Major', 'pharmacodynamic');
