INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562976, 'Aminoglutethimide', 'Triamcinolone', 'Moderate', 'Coadministration with aminoglutethimide may decrease the plasma concentrations of corticosteroids.  The proposed mechanism is induction of CYP450 3A4-mediated metabolism by aminoglutethimide.', 'DDInter', 1767317477, 'Patients should be monitored for altered corticosteroid efficacy following the initiation and discontinuation of aminoglutethimide, and the dosage adjusted accordingly to achieve desired clinical effect.  If glucocorticoid replacement is necessary, hydrocortisone may be a suitable agent because it does not appear to interact.', 'Metabolism', 'Patients should be monitored for altered corticosteroid efficacy following the initiation and discontinuation of aminoglutethimide, and the dosage adjusted accordingly to achieve desired clinical effect.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562977, 'Triamcinolone', 'Atracurium', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants.', 'DDInter', 1767317477, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.  Dose adjustments of the muscle relaxant may be required.  The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Synergism', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562978, 'Triamcinolone', 'Axicabtagene ciloleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 1767317477, 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562979, 'Triamcinolone', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562980, 'Berotralstat', 'Triamcinolone', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 1767317477, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.  Clinical and laboratory monitoring are recommended following the initiation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562981, 'Cisatracurium', 'Triamcinolone', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants.', 'DDInter', 1767317477, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.  Dose adjustments of the muscle relaxant may be required.  The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Synergism', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562982, 'Enzalutamide', 'Triamcinolone', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter.  The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction.', 'DDInter', 1767317477, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562983, 'Triamcinolone', 'Lisocabtagene maraleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 1767317477, 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562984, 'Triamcinolone', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 1767317477, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562985, 'Triamcinolone', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 1767317477, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562986, 'Yellow Fever Vaccine', 'Triamcinolone', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562987, 'Varicella Zoster Vaccine (Recombinant)', 'Triamcinolone', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.  Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'In general, the U.S. Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.  However, higher dosages, more frequent boosters, and/or serological testing may be required in some cases.  Local guidelines and prescribing information for individual vaccines should be consulted.  For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease.  For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.  For SARS-CoV-2 (COVID-19) vaccines, vaccination should preferably be completed at least two weeks before initiation or resumption of immunosuppressive therapies; however, decisions to delay or temporarily withhold immunosuppressive therapy to complete COVID-19 vaccination should consider the individual''s risks relative to their underlying condition.  Additional shots and/or boosters may be appropriate depending on age, prior COVID-19 vaccine formulation(s) received, and other factors in individuals with moderate to severe immune compromise due to medical conditions or immunosuppressive medications or treatments (e.g., solid organ transplant recipients on immunosuppressive therapy; patients on active treatment for solid tumor and hematologic malignancies).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, the U.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562988, 'Heparin (flush)', 'Fondaparinux', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 1767317477, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562989, 'Berotralstat', 'Armodafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.  Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4.', 'DDInter', 1767317477, 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.  Dose adjustments may be required if an interaction is suspected.', 'Metabolism', 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562990, 'Bromocriptine', 'Armodafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bromocriptine.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of bromocriptine should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of bromocriptine should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562991, 'Budesonide', 'Armodafinil', 'Moderate', 'Concomitant use of orally and rectally administered formulations of budesonide with inducers of CYP450 3A4 may decrease budesonide systemic as well as local exposure at the gut mucosa.  Budesonide undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of orally and rectally administered budesonide should be considered during concomitant treatment with drugs that induce CYP450 3A4.  Alternative treatments or budesonide dose adjustments may be required if an interaction is suspected.', 'Metabolism, Absorption', 'The potential for diminished pharmacologic effects of orally and rectally administered budesonide should be considered during concomitant treatment with drugs that induce CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562992, 'Fedratinib', 'Armodafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.  Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4.', 'DDInter', 1767317477, 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.  Dose adjustments may be required if an interaction is suspected.', 'Metabolism', 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562993, 'Armodafinil', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index.  Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', 'DDInter', 1767317477, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.  Dosage adjustments should be considered according to the patient''s clinical situation.  Some authorities suggest avoiding the use of ketamine in patients receiving drugs with a hypertensive effect such as ergometrine.', 'Synergism', 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562994, 'Perampanel', 'Armodafinil', 'Minor', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic.', 'DDInter', 1767317477, 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic.  Thus, clinically significant interactions would most likely be expected with drugs that have low oral bioavailability due to significant intestinal CYP450 3A4-mediated first-pass metabolism (e.g., buspirone, cyclosporine, lovastatin, midazolam, saquinavir, simvastatin, sirolimus, tacrolimus, triazolam, calcium channel blockers).  However, the potential for interaction should be considered with any drug metabolized by CYP450 3A4, especially given the high degree of interpatient variability with respect to CYP450-mediated metabolism.  Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy.  Dosage adjustments may be required if an interaction is suspected.', 'Metabolism', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562995, 'Pralsetinib', 'Armodafinil', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 1767317477, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562996, 'Relugolix', 'Armodafinil', 'Minor', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.', 'DDInter', 1767317477, 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.  Relugolix is also a substrate for intestinal P-gp.  When relugolix was coadministered with rifampin, a combined P-gp and potent CYP450 3A inducer, relugolix peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 23% and 55%, respectively.  By contrast, no clinically significant differences in the pharmacokinetics of relugolix were observed when coadministered with enzalutamide, a strong CYP450 3A inducer that is not known to induce P-gp.', 'Metabolism, Absorption', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562997, 'Tramadol', 'Armodafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tramadol, which is primarily metabolized by CYP450 3A4 and 2D6.  Reduced opioid efficacy can occur, including opioid withdrawal in physically dependent patients.', 'DDInter', 1767317477, 'Caution is advised when tramadol is used with CYP450 3A4 inducers.  Patients should be monitored for opioid efficacy and signs of withdrawal.  If a CYP450 3A4 inducer is discontinued, monitor for seizures, serotonin syndrome, sedation, and respiratory depression.', 'Metabolism', 'Caution is advised when tramadol is used with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1562998, 'Fluticasone', 'Axicabtagene ciloleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy may interfere with the therapeutic effects of such drugs as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel.  The mechanism for this interaction has not been delineated.  Administration of systemic corticosteroids in an effort to manage the toxicities associated with these drugs does not affect the expansion and persistence of CAR T cells.', 'DDInter', 1767317477, 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.  However, systemic corticosteroids may be used for the management of adverse effects associated with these drugs, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of CAR T cells.', 'Others', 'The prophylactic use of systemic corticosteroids prior to CD19- or BCMA-directed genetically modified autologous T cell immunotherapy with drugs such as axicabtagene ciloleucel, brexucabtagene autoleucel, idecabtagene vicleucel, ciltacabtagene autoleucel and lisocabtagene maraleucel is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
