INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563046, 'Etravirine', 'Morphine', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of morphine and its risk of adverse effects, including hypotension, respiratory and CNS depression, profound sedation, coma, and death.  The proposed mechanism may involve inhibition of the intestinal P-gp efflux transporter, resulting in enhanced oral bioavailability of morphine, a P-gp substrate.  The interaction may be more significant for orally administered morphine.  According to some authorities, the concomitant use of a P-gp inhibitor with oral morphine may increase the systemic exposure of morphine by approximately 2-fold.', 'DDInter', 1767317477, 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.  Close clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy, and the morphine dosage adjusted as necessary.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation that may be greater than otherwise expected.  In addition, patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them.', 'Absorption', 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563047, 'Fidaxomicin', 'Morphine', 'Moderate', 'Coadministration with fidaxomicin may increase the plasma concentrations of some orally administered drugs that are substrates of the P-glycoprotein (P-gp) transporter.  In vitro, fidaxomicin and its main metabolite are substrates and inhibitors of P-gp, an efflux transporter expressed in the gastrointestinal tract.', 'DDInter', 1767317477, 'Caution is advised when fidaxomicin is used in combination with P-gp substrates that may be sensitive to intestinal P-gp inhibition.', 'Absorption', 'Caution is advised when fidaxomicin is used in combination with P-gp substrates that may be sensitive to intestinal P-gp inhibition.', NULL, NULL, 'Moderate', 'pharmacodynamic');
