INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563079, 'Vandetanib', 'Morphine', 'Moderate', 'Coadministration with vandetanib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein transporter, such as digoxin and dabigatran.  The mechanism involves increased absorption and/or decreased clearance due to inhibition of drug efflux mediated by intestinal and renal/hepatic P-glycoprotein, respectively.  Vandetanib is a weak P-glycoprotein inhibitor.', 'DDInter', 1767317477, 'Caution is advised if vandetanib must be used concomitantly with medications that are substrates of P-glycoprotein, particularly those with a narrow therapeutic range such as digoxin.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vandetanib is added to or withdrawn from therapy.', 'Absorption, Excretion', 'Caution is advised if vandetanib must be used concomitantly with medications that are substrates of P-glycoprotein, particularly those with a narrow therapeutic range such as digoxin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
