    ) VALUES (1563362, 'Aminoglutethimide', 'Betamethasone', 'Moderate', 'Coadministration with aminoglutethimide may decrease the plasma concentrations of corticosteroids.  The proposed mechanism is induction of CYP450 3A4-mediated metabolism by aminoglutethimide.', NULL, 'Patients should be monitored for altered corticosteroid efficacy following the initiation and discontinuation of aminoglutethimide, and the dosage adjusted accordingly to achieve desired clinical effect.', NULL, 'Patients should be monitored for altered corticosteroid efficacy following the initiation and discontinuation of aminoglutethimide, and the dosage adjusted accordingly to achieve desired clinical effect.  If glucocorticoid replacement is necessary, hydrocortisone may be a suitable agent because it does not appear to interact.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
