INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563434, 'Fedratinib', 'Bicalutamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fedratinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19 and flavin-containing monooxygenase 3 (FMO3).', 'DDInter', 1767317477, 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.g., amiodarone, cimetidine, fexinidazole, isoniazid, osilodrostat, rucaparib) or if combined with other drugs that can inhibit CYP450 2C19 (e.g., esomeprazole, fenofibrate, fluoxetine, lansoprazole, letrozole, moclobemide, omeprazole, parecoxib, ticlopidine).  Patients should be monitored for adverse reactions such as nausea, vomiting, diarrhea, anemia, thrombocytopenia, neutropenia, encephalopathy (including Wernicke''s), liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations, increased blood creatinine, and secondary malignancies.  Dosage adjustment or treatment interruption may be required based on patient tolerance and response in accordance with the prescribing information.', 'Metabolism', 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
