INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563873, 'Idelalisib', 'Lisocabtagene maraleucel', 'Moderate', 'The risk and/or severity of diarrhea may be increased when idelalisib is used with other agents that cause diarrhea.', 'DDInter', 1767317477, 'Concomitant use of idelalisib with other drugs that cause diarrhea should be avoided whenever possible.  Patients treated with idelalisib should be monitored for development of severe diarrhea or colitis during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.  Permanent discontinuation of idelalisib is recommended in those who experience life-threatening diarrhea or intestinal perforation.  Patients should be advised to contact their physician immediately if the number of bowel movements per day increases by six or more, and to promptly report any new or worsening abdominal pain, chills, fever, nausea, or vomiting.', 'Synergism', 'Concomitant use of idelalisib with other drugs that cause diarrhea should be avoided whenever possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
