INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563919, 'Ceritinib', 'Bromocriptine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.', 'DDInter', 1767317477, 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.  In patients who have been treated with a potent CYP450 3A4 inhibitor, an adequate washout of the inhibitor (approximately 3 to 5 elimination half-lives) is recommended prior to initiation of bromocriptine.  If coadministration with a potent CYP450 3A4 inhibitor is required, bromocriptine dosage should be reduced to avoid toxicity.  Caution and close monitoring for development of adverse effects are advisable, and the bromocriptine dosage further adjusted if necessary.', 'Metabolism', 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563920, 'Chloramphenicol', 'Bromocriptine', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.  As such, inhibitors of CYP450 3A4 may markedly reduce the metabolic clearance of bromocriptine.', 'DDInter', 1767317477, 'Caution and close monitoring for development of adverse effects are advisable during coadministration of bromocriptine with moderate CYP450 3A4 inhibitors.  Bromocriptine dosage may need to be reduced to avoid toxicity.  The prescribing information for Cycloset, a bromocriptine product indicated for use in adults with type 2 diabetes mellitus to improve glycemic control, recommends limiting the dose to 1.6 mg daily during concomitant use of a moderate CYP450 3A4 inhibitor.', 'Metabolism', 'Caution and close monitoring for development of adverse effects are advisable during coadministration of bromocriptine with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
