INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1563982, 'Cisatracurium', 'Prednisolone', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants.', 'DDInter', 1767317477, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.  Dose adjustments of the muscle relaxant may be required.  The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Synergism', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
