INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564168, 'Drospirenone', 'Octreotide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564169, 'Octreotide', 'Lemborexant', 'Major', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The lemborexant dose should be limited to 5 mg no more than once per night when coadministered with weak CYP450 3A4 inhibitors.', 'Metabolism', 'The lemborexant dose should be limited to 5 mg no more than once per night when coadministered with weak CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564170, 'Octreotide', 'Lumateperone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lumateperone.  In vitro studies show that multiple enzymes are involved in the metabolism of lumateperone, including but not limited to uridine 5''-diphospho-glucuronosyltransferases (UGT) 1A1, 1A4, and 2B15; aldoketoreductase (AKR) 1C1, 1B10, and 1C4; and cytochrome P450 (CYP450) 3A4, 2C8, and 1A2.', 'DDInter', 1767317477, 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.  Patients should be monitored for increased adverse effects such as extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia, neutropenia, leukopenia, orthostatic hypotension, syncope, seizures, cognitive and motor impairment, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Caution is advised when lumateperone is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564171, 'Dexmethylphenidate', 'Nebivolol', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', 'DDInter', 1767317477, 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564172, 'Pyrophosphoric acid', 'Nebivolol', 'Moderate', 'Medicinal products including sodium heparin, prazosin, methyldopa, hydralazine, quinidine, beta blockers, calcium channel blockers, digitalis glycosides, nitrates, anthracyclines, and iodinated contrast agents may interfere with the blood pool images taken using Technetium Tc 99m pyrophosphate.  This is characterized by a reduction in the amount of injected radioactivity remaining in the blood pool.', 'DDInter', 1767317477, 'Clinicians should be aware of the possibility of a diagnostic interference.  When use of iodinated contrast media is anticipated, it is recommended to perform the scintigraphy with (99mTc)-labeled red blood cells prior to administration of the iodinated contrast media to avoid adversely affecting the red blood cell labeling.', 'Distribution', 'Clinicians should be aware of the possibility of a diagnostic interference.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564173, 'Cabazitaxel', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564174, 'Fedratinib', 'Cabazitaxel', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors.  Pharmacologic response to cabazitaxel should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the cabazitaxel dosage adjusted as necessary.  Regular monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience potential signs and symptoms of cabazitaxel toxicity such as infection, fever, chills, diarrhea, unusual bruising or bleeding, and burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564175, 'Cabazitaxel', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 1767317477, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
