INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564183, 'Verapamil', 'Melatonin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may substantially increase the plasma concentrations of melatonin.  Data from available studies suggest that melatonin is primarily metabolized by CYP450 1A2, with possible contribution from CYP450 2C19 and 2C9.  Coadministration of fluvoxamine, a potent CYP450 1A2 inhibitor that also inhibits CYP450 2C19 and 2C9, increased melatonin systemic exposure (AUC) by 17-fold and maximum plasma concentration (Cmax) by 12-fold.  Contraceptives containing ethinyl estradiol, which can inhibit CYP450 1A2, may lead to a 4- to 5-fold increase in melatonin concentration.', 'DDInter', 1767317477, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.  Patients should be monitored closely for adverse effects such as excessive drowsiness, headache, lethargy, dizziness, irritability, nervousness, restlessness, anxiety, abdominal pain, and dyspepsia.', 'Metabolism', 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564184, 'Verapamil', 'Pyrophosphoric acid', 'Moderate', 'Medicinal products including sodium heparin, prazosin, methyldopa, hydralazine, quinidine, beta blockers, calcium channel blockers, digitalis glycosides, nitrates, anthracyclines, and iodinated contrast agents may interfere with the blood pool images taken using Technetium Tc 99m pyrophosphate.  This is characterized by a reduction in the amount of injected radioactivity remaining in the blood pool.', 'DDInter', 1767317477, 'Clinicians should be aware of the possibility of a diagnostic interference.  When use of iodinated contrast media is anticipated, it is recommended to perform the scintigraphy with (99mTc)-labeled red blood cells prior to administration of the iodinated contrast media to avoid adversely affecting the red blood cell labeling.', 'Distribution', 'Clinicians should be aware of the possibility of a diagnostic interference.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564185, 'Calcitriol', 'Calcitriol (topical)', 'Moderate', 'Elevations in serum calcium levels have been observed in patients treated with topical vitamin D analogs.  Concomitant use with systemic vitamin D products, calcium supplements, or other agents that can increase serum calcium concentrations such as thiazide diuretics, estrogens, anabolic steroids, and parathyroid hormone or parathyroid hormone analogs may increase the risk of clinically significant hypercalcemia.', 'DDInter', 1767317477, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', 'Synergism', 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564186, 'Elvitegravir', 'Calcium acetate', 'Moderate', 'The concomitant use of antacids or oral products containing polyvalent cations (e.g., Mg, Al, Ca, Fe, Zn) may significantly decrease the plasma concentrations of elvitegravir.  According to the elvitegravir manufacturer, the mechanism of the interaction may be related to local complexation in the gastrointestinal tract and not to changes in gastric pH.', 'DDInter', 1767317477, 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.  Some experts recommend an interval of at least 4 hours.  Alternatively, H2-receptor antagonists or proton pump inhibitors may be considered instead of antacids, with no dosage adjustment needed.', 'Absorption', 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564187, 'Elvitegravir', 'Calcium citrate', 'Moderate', 'The concomitant use of antacids or oral products containing polyvalent cations (e.g., Mg, Al, Ca, Fe, Zn) may significantly decrease the plasma concentrations of elvitegravir.  According to the elvitegravir manufacturer, the mechanism of the interaction may be related to local complexation in the gastrointestinal tract and not to changes in gastric pH.', 'DDInter', 1767317477, 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.  Some experts recommend an interval of at least 4 hours.  Alternatively, H2-receptor antagonists or proton pump inhibitors may be considered instead of antacids, with no dosage adjustment needed.', 'Absorption', 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
