INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564215, 'Cannabidiol', 'Lefamulin', 'Minor', 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19.  Cannabidiol is metabolized in the liver and the gut (primarily in the liver) by CYP450 2C19 and CYP450 3A4 enzymes, and UGT1A7, UGT1A9, and UGT2B7 isoforms.', 'DDInter', 1767317477, 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19.  Cannabidiol is metabolized in the liver and the gut (primarily in the liver) by CYP450 2C19 and CYP450 3A4 enzymes, and UGT1A7, UGT1A9, and UGT2B7 isoforms.  Coadministration of cannabidiol with itraconazole (a potent inhibitor of CYP450 3A4) increased cannabidiol exposure by less than 10%; exposure to fluconazole (a potent CYP450 2C19 inhibitor) was increased to less than 20%.  Both values are not considered clinically meaningful.', 'Metabolism', 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19.', NULL, NULL, 'Minor', 'pharmacodynamic');
