INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564308, 'Morphine', 'Mepivacaine', 'Moderate', 'Concomitant epidural administration of local anesthetics may affect the release of morphine sulfate from the liposomal formulation.  The mechanism of interaction is unknown.', 'DDInter', 1767317477, 'To minimize the potential for interaction between liposomal morphine and lidocaine-epinephrine test dose (1.5%-1:200,000, 3 mL), flush the epidural catheter with 1 mL of preservative-free 0.9% normal saline and wait at least 15 minutes after the test dose before administering the morphine.  Following administration of an analgesic dose of bupivacaine (0.25%, 20 mL), flush the epidural catheter with 1 mL of preservative-free 0.9% normal saline and wait at least 30 minutes before administering the morphine.  Liposomal morphine should not be mixed with any other medications.  Once it has been administered, no other medication should be administered into the epidural space for at least 48 hours.', 'Absorption', 'To minimize the potential for interaction between liposomal morphine and lidocaine-epinephrine test dose (1.', NULL, NULL, 'Moderate', 'pharmacodynamic');
