INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564325, 'Iron', 'Elvitegravir', 'Moderate', 'The concomitant use of antacids or oral products containing polyvalent cations (e.g., Mg, Al, Ca, Fe, Zn) may significantly decrease the plasma concentrations of elvitegravir.  According to the elvitegravir manufacturer, the mechanism of the interaction may be related to local complexation in the gastrointestinal tract and not to changes in gastric pH.', 'DDInter', 1767317477, 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.  Some experts recommend an interval of at least 4 hours.  Alternatively, H2-receptor antagonists or proton pump inhibitors may be considered instead of antacids, with no dosage adjustment needed.', 'Absorption', 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564326, 'Carboplatin', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564327, 'Colistimethate', 'Carboplatin', 'Moderate', 'Increased adverse effects are possible when glycopeptide, polypeptide, or polymyxin antibiotics are administered concomitantly with other potentially nephrotoxic and neurotoxic drugs.', 'DDInter', 1767317477, 'If these drugs must be used together, renal function, auditory and vestibular function, and serum drug concentrations should be monitored.', 'Synergism', 'If these drugs must be used together, renal function, auditory and vestibular function, and serum drug concentrations should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564328, 'Ibritumomab tiuxetan', 'Carboplatin', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', 'DDInter', 1767317477, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.  Dosing adjustments may be necessary.  The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, NULL, 'Moderate', 'pharmacodynamic');
