INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564419, 'Citalopram', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564420, 'Citalopram', 'Turmeric', 'Moderate', 'Turmeric may potentiate the bleeding risk associated with drugs that interfere with platelet function or coagulation.', 'DDInter', 1767317477, 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.  Clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564421, 'Bacillus calmette-guerin substrain tice live antigen', 'Mycophenolate mofetil', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564422, 'Clozapine', 'Mycophenolate mofetil', 'Major', 'Coadministration of clozapine with other agents that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', 'DDInter', 1767317477, 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.  Agents that may be significantly myelotoxic include antineoplastic drugs, some anticonvulsant and antirheumatic medications, albendazole, chloramphenicol, colchicine, dapsone, interferons, linezolid, pentamidine, procainamide, and zidovudine.', 'Synergism', 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.', NULL, NULL, 'Major', 'pharmacodynamic');
