INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564453, 'Yellow Fever Vaccine', 'Tetracosactide', 'Moderate', 'The administration of live, attenuated virus or bacterial vaccines during corticosteroid therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored.  The interval depends on the dosage and duration of corticosteroid therapy administered, but may be at least 3 months in most cases.  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, high-dose corticosteroid therapy should not be initiated for at least 2 weeks.  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Synergism', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
