INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564721, 'Tramadol', 'Tazemetostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tramadol, which is primarily metabolized by CYP450 3A4 and 2D6.  Reduced opioid efficacy can occur, including opioid withdrawal in physically dependent patients.', 'DDInter', 1767317477, 'Caution is advised when tramadol is used with CYP450 3A4 inducers.  Patients should be monitored for opioid efficacy and signs of withdrawal.  If a CYP450 3A4 inducer is discontinued, monitor for seizures, serotonin syndrome, sedation, and respiratory depression.', 'Metabolism', 'Caution is advised when tramadol is used with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
