INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564750, 'Gatifloxacin', 'Axicabtagene ciloleucel', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 1767317477, 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.  In six patients with newly diagnosed hematologic malignancy, treatment with various antineoplastic agents (cyclophosphamide, cytarabine, daunorubicin, doxorubicin, mitoxantrone, prednisolone, vincristine) decreased the mean peak serum concentration (Cmax) and area under the concentration-time curve (AUC 0 to 4 hours) of ciprofloxacin by approximately 46% each.  Data are not available for other quinolone antibiotics.', 'Absorption', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564751, 'Gatifloxacin', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564752, 'Anisindione', 'Oxytetracycline', 'Moderate', 'Tetracyclines may increase the hypoprothrombinemic effects of warfarin and similar anticoagulants.  The mechanism of interaction has not been established, but may involve depression of plasma prothrombin activity or suppression of vitamin K-producing bacteria in the gastrointestinal tract, although the latter may be of limited clinical importance except in patients whose dietary intake of vitamin K is severely reduced.', 'DDInter', 1767317477, 'Caution is advised when warfarin and similar anticoagulants are used with tetracyclines.  The INR should be checked frequently and the anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of tetracycline therapy in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, 'Caution is advised when warfarin and similar anticoagulants are used with tetracyclines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564753, 'Attapulgite', 'Oxytetracycline', 'Moderate', 'Administration of a tetracycline with aluminum, calcium, or magnesium salts significantly decreases tetracycline serum concentrations.  The proposed mechanism is chelation of tetracycline by the cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.  When coadministered with Suprep Bowel Prep (magnesium/potassium/sodium sulfates), the manufacturer recommends administering tetracycline antibiotics at least 2 hours before and not less than 6 hours after Suprep Bowel Prep to avoid chelation with magnesium.', 'Absorption', 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564754, 'Bacampicillin', 'Oxytetracycline', 'Moderate', 'Tetracyclines may reduce the effect of penicillins by inhibiting cellular protein synthesis which is necessary for cell wall synthesis inhibition by penicillins.  Antagonism is more likely when low doses of either agent are administered.  Therapeutic failure may result.', 'DDInter', 1767317477, 'This combination should be avoided if possible.', 'Antagonism', 'This combination should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564755, 'Bendroflumethiazide', 'Oxytetracycline', 'Minor', 'The coadministration of diuretics and tetracyclines may result in decreased renal function manifested by increases in serum creatinine and blood urea nitrogen (BUN).', 'DDInter', 1767317477, 'Usually, no clinical intervention is necessary, unless decreases in renal function occur.  If renal function deteriorates, discontinuation of one or both agents may be necessary.', 'Synergism', 'Usually, no clinical intervention is necessary, unless decreases in renal function occur.', NULL, NULL, 'Minor', 'pharmacodynamic');
