INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564762, 'Ergometrine', 'Oxytetracycline', 'Moderate', 'Limited data suggest that tetracyclines may increase the plasma concentrations and toxicity of ergot alkaloids.', 'DDInter', 1767317477, 'Caution may be advisable when ergot alkaloids are prescribed in combination with tetracyclines.  Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.  Patients should be advised to notify their physician if they experience potential signs and symptoms of ergotism such as paraesthesia, cyanosis, cold or painful extremities, muscle pain or weakness, abdominal pain, irregular heartbeat, and chest pain or tightness.', 'Synergism', 'Caution may be advisable when ergot alkaloids are prescribed in combination with tetracyclines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564763, 'Insulin human', 'Oxytetracycline', 'Moderate', 'Tetracyclines may enhance the hypoglycemic effect of insulin by increasing tissue sensitivity to exogenous insulin.', 'DDInter', 1767317477, 'Blood glucose should be monitored closely whenever tetracyclines are prescribed to patients on insulin, especially if they are elderly or have renal impairment.  The insulin dosage may require adjustment if an interaction is suspected.  Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Synergism', 'Blood glucose should be monitored closely whenever tetracyclines are prescribed to patients on insulin, especially if they are elderly or have renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
