INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564869, 'Tocainide', 'Oxtriphylline', 'Minor', 'Tocainide causes a decrease in theophylline clearance.  The result is an increase in serum theophylline concentrations and a mild increase in the serum half-life of theophylline.The mechanism of this interaction is not known.', 'DDInter', 1767317477, 'Management consists of monitoring clinical status during therapy and checking serum theophylline levels as necessary.  Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Others', 'Management consists of monitoring clinical status during therapy and checking serum theophylline levels as necessary.', NULL, NULL, 'Minor', 'pharmacodynamic');
