INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564871, 'Daclatasvir', 'Tolbutamide', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
