INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564888, 'Triptorelin', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1564889, 'Heparin (flush)', 'Pentoxifylline', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', 'DDInter', 1767317477, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', 'Caution and monitoring (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
