    ) VALUES (1564953, 'Pexidartinib', 'Neratinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.', NULL, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.  According to some authorities, if the CYP450 3A4 inducer cannot be avoided, the dose of neratinib should be increased to 320 mg once daily.  The daily dose of neratinib should not exceed 320 mg.  The previous dose of neratinib may be resumed following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
