        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1565453, 'Duvelisib', 'Propafenone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of propafenone, which may result in proarrhythmic events and exaggerated beta-adrenergic blocking activity.', NULL, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.  Therapeutic response to propafenone as well as clinical and electrocardiographic evidence of toxicity should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the propafenone dosage adjusted as necessary.  Patients should be monitored for potential adverse effects such as new or worsening arrhythmias or heart failure, edema, bradycardia, and atrioventricular block.  Simultaneous use of propafenone with both a CYP450 3A4 inhibitor and a CYP450 2D6 inhibitor (or in patients with CYP450 2D6 deficiency) should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
