INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565494, 'Cyclophosphamide', 'Cinchocaine (topical)', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes.  Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk.', 'DDInter', 1767317477, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.  Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route.  Signs and symptoms of methemoglobinemia may be delayed some hours after drug exposure.  Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.  In severe cases, patients may progress to central nervous system depression, stupor, seizures, acidosis, cardiac arrhythmias, syncope, shock, coma, and death.  Methemoglobinemia should be considered if central cyanosis is unresponsive to oxygen.  Calculated oxygen saturation and pulse oximetry are generally not accurate in the setting of methemoglobinemia.  The diagnosis can be confirmed by an elevated methemoglobin level of at least 10% using co-oximetry.  Methemoglobin concentrations greater than 10% of total hemoglobin will typically cause cyanosis, and levels over 70% are frequently fatal.  However, symptom severity is not always related to methemoglobin levels.  Mild cases often respond to withdrawal of offending agents and symptomatic support.  If patient does not respond to administration of oxygen, clinically significant or symptomatic methemoglobinemia can be treated with methylene blue 1 to 2 mg/kg by slow intravenous injection over 5 to 10 minutes, which may be repeated within 30 to 60 minutes if necessary.  Higher dosages of methylene blue (usually greater than 7 mg/kg) should be avoided, as it can paradoxically exacerbate methemoglobinemia.  Additionally, methylene blue is ineffective and can cause hemolytic anemia in patients with G6PD deficiency.  These patients may be treated with exchange transfusion, dialysis, and/or hyperbaric oxygenation in addition to symptomatic support.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565495, 'Etidocaine', 'Cyclophosphamide', 'Major', 'Coadministration of local anesthetics with other oxidizing agents that can also induce methemoglobinemia such as antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk.', 'DDInter', 1767317477, 'Close monitoring for signs and symptoms of methemoglobinemia is recommended if local anesthetics must be used with other methemoglobin-inducing agents.  Signs and symptoms of methemoglobinemia may occur immediately or hours after drug exposure.  Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia (e.g., cyanotic skin discoloration, abnormal blood coloration, nausea, headache, dizziness, lightheadedness, lethargy, fatigue, dyspnea, tachypnea, tachycardia, palpitation, anxiety, and confusion).  In severe cases, patients may progress to central nervous system depression, stupor, seizures, acidosis, cardiac arrhythmias, syncope, shock, coma, and death.  Methemoglobinemia should be considered if central cyanosis is unresponsive to oxygen.  Calculated oxygen saturation and pulse oximetry are generally not accurate in the setting of methemoglobinemia.  The diagnosis can be confirmed by an elevated methemoglobin level of at least 10% using co-oximetry.  Methemoglobin concentrations greater than 10% of total hemoglobin will typically cause cyanosis, and levels over 70% are frequently fatal.  However, symptom severity is not always related to methemoglobin levels.  Mild cases often respond to withdrawal of offending agents and symptomatic support.  If a patient does not respond to administration of oxygen, clinically significant or symptomatic methemoglobinemia can be treated with methylene blue 1 to 2 mg/kg by slow intravenous injection over 5 to 10 minutes, which may be repeated within 30 to 60 minutes if necessary.  Higher dosages of methylene blue (usually greater than 7 mg/kg) should be avoided, as it can paradoxically exacerbate methemoglobinemia.  Additionally, methylene blue is ineffective and can cause hemolytic anemia in patients with G6PD deficiency.  These patients may be treated with exchange transfusion, dialysis, and/or hyperbaric oxygenation in addition to symptomatic support.', 'Synergism', 'Close monitoring for signs and symptoms of methemoglobinemia is recommended if local anesthetics must be used with other methemoglobin-inducing agents.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565496, 'Ibritumomab tiuxetan', 'Cyclophosphamide', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', 'DDInter', 1767317477, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.  Dosing adjustments may be necessary.  The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, NULL, 'Moderate', 'pharmacodynamic');
