INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565662, 'Elvitegravir', 'Attapulgite', 'Moderate', 'The concomitant use of antacids or oral products containing polyvalent cations (e.g., Mg, Al, Ca, Fe, Zn) may significantly decrease the plasma concentrations of elvitegravir.  According to the elvitegravir manufacturer, the mechanism of the interaction may be related to local complexation in the gastrointestinal tract and not to changes in gastric pH.', 'DDInter', 1767317477, 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.  Some experts recommend an interval of at least 4 hours.  Alternatively, H2-receptor antagonists or proton pump inhibitors may be considered instead of antacids, with no dosage adjustment needed.', 'Absorption', 'Antacid and oral products containing polyvalent cations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
