INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565682, 'Selumetinib', 'Methotrexate', 'Moderate', 'Coadministration with selumetinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic anion transporter 3 (OAT3).  The proposed mechanism, based on in vivo data, is decreased clearance due to selumetinib-mediated inhibition of OAT3 transporter.', 'DDInter', 1767317477, 'Caution is advised if selumetinib is used concomitantly with drugs that are substrates of OAT3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring of the OAT3 substrate should be considered whenever selumetinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if selumetinib is used concomitantly with drugs that are substrates of OAT3, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
