INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565759, 'Entrectinib', 'Oxycodone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of oxycodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution may be advisable when oxycodone is prescribed with weak CYP450 3A4 inhibitors.  The risk of overdose may be increased following the initiation of a CYP450 3A4 inhibitor in patients already receiving oxycodone.  Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension.  Following discontinuation of the CYP450 3A4 inhibitor, patients should be monitored for reduced efficacy of oxycodone or development of withdrawal symptoms due to reduced plasma oxycodone levels.', 'Metabolism', 'Caution may be advisable when oxycodone is prescribed with weak CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
