INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565833, 'Dextroamphetamine', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index.  Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', 'DDInter', 1767317477, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.  Dosage adjustments should be considered according to the patient''s clinical situation.  Some authorities suggest avoiding the use of ketamine in patients receiving drugs with a hypertensive effect such as ergometrine.', 'Synergism', 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565834, 'Dextroamphetamine', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme.  The mechanism is decreased clearance due to competitive inhibition of CYP450 2D6 activity by ritonavir.', 'DDInter', 1767317477, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565835, 'Dexlansoprazole', 'Pemigatinib', 'Moderate', 'Coadministration of pemigatinib with proton pump inhibitors may result in a reduction in the plasma concentrations of pemigatinib.  The mechanism for this interaction has not been delineated.', 'DDInter', 1767317477, 'Until more information is available, caution is recommended when pemigatinib is used concomitantly with proton pump inhibitors.  Patients should be monitored for potentially reduced efficacy.', 'Others', 'Until more information is available, caution is recommended when pemigatinib is used concomitantly with proton pump inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
