INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565900, 'Etidronic acid', 'Moxetumomab Pasudotox', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney.', 'DDInter', 1767317477, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents).  Renal function and serum electrolytes should be monitored.  Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.  In those treated for bone metastases, treatment should not be resumed until renal function returns to baseline.', 'Synergism', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565901, 'Diethylpropion', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index.  Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', 'DDInter', 1767317477, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.  Dosage adjustments should be considered according to the patient''s clinical situation.  Some authorities suggest avoiding the use of ketamine in patients receiving drugs with a hypertensive effect such as ergometrine.', 'Synergism', 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565902, 'Fidaxomicin', 'Alvimopan', 'Moderate', 'Coadministration with fidaxomicin may increase the plasma concentrations of some orally administered drugs that are substrates of the P-glycoprotein (P-gp) transporter.  In vitro, fidaxomicin and its main metabolite are substrates and inhibitors of P-gp, an efflux transporter expressed in the gastrointestinal tract.', 'DDInter', 1767317477, 'Caution is advised when fidaxomicin is used in combination with P-gp substrates that may be sensitive to intestinal P-gp inhibition.', 'Absorption', 'Caution is advised when fidaxomicin is used in combination with P-gp substrates that may be sensitive to intestinal P-gp inhibition.', NULL, NULL, 'Moderate', 'pharmacodynamic');
