INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565976, 'Dimethyl fumarate', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565977, 'Echinacea', 'Dimethyl fumarate', 'Minor', 'Theoretically, agents that are thought to have immunostimulant properties such as echinacea, vitamin E, cat''s claw, and zinc may antagonize the pharmacologic effects of immunosuppressants.  However, clinical cases of drug interactions have not been reported.', 'DDInter', 1767317477, 'The effect of this interaction is not well established, and the possibility of an interaction should be considered if an adverse event occurs when combining Cat''s Claw with an immune-related drug.', 'Others', 'The effect of this interaction is not well established, and the possibility of an interaction should be considered if an adverse event occurs when combining Cat''s Claw with an immune-related drug.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1565978, 'Dimethyl fumarate', 'Melphalan flufenamide', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.  Treatment with MMF or its prodrugs can cause lymphopenia as well as serious and potentially life-threatening opportunistic infections (e.g., progressive multifocal leukoencephalopathy (PML), herpes zoster, herpes simplex, West Nile virus, cytomegalovirus, Candida, Aspergillus, Nocardia, Listeria monocytogenes, Mycobacterium tuberculosis), and the risk may be theoretically potentiated in the presence of other agents that also suppress the immune system or bone marrow function.', 'DDInter', 1767317477, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.  Until further information is available, concomitant use should be avoided if possible.', 'Synergism', 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
