    ) VALUES (1566525, 'Fesoterodine', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.  Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
