    ) VALUES (1567001, 'Romidepsin', 'Phenylbutyric acid', 'Moderate', 'Coadministration of histone deacetylase (HDAC) inhibitors and phenylbutyrate may lead to excess inhibition of HDAC and additive adverse effects.  According to the prescribing information for sodium phenylbutyrate combined with taurursodiol (ursodoxicoltaurine), phenylbutyrate is a pan-HDAC inhibitor', NULL, 'The prescribing information for sodium phenylbutyrate combined with taurursodiol recommends avoiding concomitant use of phenylbutyrate and other HDAC inhibitors.', NULL, 'The prescribing information for sodium phenylbutyrate combined with taurursodiol recommends avoiding concomitant use of phenylbutyrate and other HDAC inhibitors.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
