        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1567086, 'Ranitidine (bismuth citrate)', 'Ketoconazole', 'Moderate', 'H2 antagonists may reduce the bioavailability of ketoconazole by 75% to 80% resulting in decreased plasma ketoconazole concentrations and possible therapeutic failure.  The mechanism is related to an increase in gastric pH and a decrease in absorption of the antifungal agent.', NULL, 'Some manufacturers of ketoconazole suggest administering ketoconazole with an acidic beverage (e.', NULL, 'Some manufacturers of ketoconazole suggest administering ketoconazole with an acidic beverage (e.g., non-diet cola, orange juice) if used concomitantly with H2 antagonists (US, GB, CA, BR).  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever H2 antagonists are added to or withdrawn from therapy with ketoconazole.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
