        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1567090, 'Telaprevir', 'Ketoconazole', 'Moderate', 'Coadministration of an azole antifungal agent and hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) may result in increased plasma concentrations of both drugs.  The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors.  Dosage adjustments may be required.  According to the product information for boceprevir and telaprevir, high dosages of ketoconazole and itraconazole (>200 mg/day) should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
