    ) VALUES (1567456, 'Troleandomycin', 'Telaprevir', 'Moderate', 'Coadministration of a macrolide or ketolide antibiotic (clarithromycin, erythromycin, telithromycin) with a hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) is expected to increase the plasma concentrations of both drugs.  The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme.', NULL, 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with a HCV NS3/4A protease inhibitor.', NULL, 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with a HCV NS3/4A protease inhibitor.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
