INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567596, 'Relugolix', 'Phenolphthalein', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval.', 'DDInter', 1767317477, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.  The recommended dosage and duration of use should not be exceeded.  Patients treated with lactulose for more than six months should be monitored periodically for electrolyte imbalance.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567597, 'Relugolix', 'Phenylbutazone', 'Minor', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.', 'DDInter', 1767317477, 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.  Relugolix is also a substrate for intestinal P-gp.  When relugolix was coadministered with rifampin, a combined P-gp and potent CYP450 3A inducer, relugolix peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 23% and 55%, respectively.  By contrast, no clinically significant differences in the pharmacokinetics of relugolix were observed when coadministered with enzalutamide, a strong CYP450 3A inducer that is not known to induce P-gp.', 'Metabolism, Absorption', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.', NULL, NULL, 'Minor', 'pharmacodynamic');
