INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567638, 'Anisindione', 'Interferon alfa-2a, Recombinant', 'Moderate', 'Interferons may increase the plasma concentrations and pharmacologic effects of warfarin and other oral anticoagulants.  At least one case of this interaction has been reported in the literature.  The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes.  Physiologic events and drugs that induce interferon synthesis (e.g., viral infection, influenza vaccination) have also been reported to decrease metabolism and potentiate the effects of certain drugs including warfarin.', 'DDInter', 1767317477, 'Caution is advised if oral anticoagulants are administered with an interferon.  The INR should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Caution is advised if oral anticoagulants are administered with an interferon.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567639, 'Interferon alfa-2a, Recombinant', 'Aurothioglucose', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of agents with neurotoxic effects.  Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.  Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded.  Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.  If feasible, therapy should generally be reinstituted only after resolution of neuropathy symptoms or return of symptoms to baseline status.  In some cases, permanent dosage reductions may be required.', 'Synergism', 'Caution is advised during concomitant use of agents with neurotoxic effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
