INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567685, 'Phenylbutazone', 'Nalidixic acid', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use.  The interaction has been reported most often with enoxacin.  It may occur with other fluoroquinolones as well, but is poorly documented.  The exact mechanism of interaction is unknown.  Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect.  Patients with a history of seizures may be at greater risk.', 'DDInter', 1767317477, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567686, 'Plicamycin', 'Nalidixic acid', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 1767317477, 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.  The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.  In six patients with newly diagnosed hematologic malignancy, treatment with various antineoplastic agents (cyclophosphamide, cytarabine, daunorubicin, doxorubicin, mitoxantrone, prednisolone, vincristine) decreased the mean peak serum concentration (Cmax) and area under the concentration-time curve (AUC 0 to 4 hours) of ciprofloxacin by approximately 46% each.  Data are not available for other quinolone antibiotics.', 'Absorption', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics.', NULL, NULL, 'Minor', 'pharmacodynamic');
