INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567740, 'Urokinase', 'Rofecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', 'DDInter', 1767317477, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.  Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567741, 'Urokinase', 'Tositumomab', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317477, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.  Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.  A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for a minimum of 10 weeks after treatment.', 'Synergism', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567742, 'Siponimod', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during siponimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.  Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 1767317477, 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination.  Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with siponimod.  For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with siponimod.  In patients who have recently been vaccinated, siponimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Synergism', 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567743, 'Siponimod', 'Melphalan flufenamide', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may result in additive immune system effects and increased risk of infections.  Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 1767317477, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or other immune-modulating agents have not been evaluated.  Close monitoring for signs and symptoms of infection is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod.  When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or other immune-modulating agents have not been evaluated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567744, 'Alimemazine', 'Siponimod', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of siponimod treatment in patients receiving drugs that prolong the QT interval.', 'DDInter', 1767317477, 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.  Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with siponimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.  Advice from a cardiologist should be sought if treatment with siponimod is considered in patients on concurrent therapy with QT prolonging drugs with a known risk of torsades de pointes or drugs that slow heart rate or AV conduction.', 'Synergism', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567745, 'Varicella Zoster Vaccine (Recombinant)', 'Siponimod', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines during siponimod therapy is generally safe but may be associated with a diminished or suboptimal immunologic response.  Vaccination may be less effective during and for up to one month after discontinuation of siponimod therapy.', 'DDInter', 1767317477, 'Immune response to vaccines may be reduced during and for up to one month after discontinuation of siponimod therapy.  Treatment with siponimod should be paused 1 week prior to and for 4 weeks after vaccination.', 'Antagonism', 'Immune response to vaccines may be reduced during and for up to one month after discontinuation of siponimod therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567746, 'Acrivastine', 'Methylphenobarbital', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Cautious dosage titration may be required, particularly at treatment initiation.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
