INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567791, 'Rofecoxib', 'Melatonin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may substantially increase the plasma concentrations of melatonin.  Data from available studies suggest that melatonin is primarily metabolized by CYP450 1A2, with possible contribution from CYP450 2C19 and 2C9.  Coadministration of fluvoxamine, a potent CYP450 1A2 inhibitor that also inhibits CYP450 2C19 and 2C9, increased melatonin systemic exposure (AUC) by 17-fold and maximum plasma concentration (Cmax) by 12-fold.  Contraceptives containing ethinyl estradiol, which can inhibit CYP450 1A2, may lead to a 4- to 5-fold increase in melatonin concentration.', 'DDInter', 1767317477, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.  Patients should be monitored closely for adverse effects such as excessive drowsiness, headache, lethargy, dizziness, irritability, nervousness, restlessness, anxiety, abdominal pain, and dyspepsia.', 'Metabolism', 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
