INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1567839, 'Botulinum toxin type A', 'Methocarbamol', 'Moderate', 'Excessive muscle weakness associated with the use of botulism toxin may be exaggerated by concomitant use of a skeletal muscle relaxant.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings and is a neuromuscular blocking agent, thus additive effects may occur with muscle relaxants.', 'DDInter', 1767317477, 'Patients should be advised that muscle weakness may be increased when botulinum toxin is used in combination with skeletal muscle relaxants.', 'Synergism', 'Patients should be advised that muscle weakness may be increased when botulinum toxin is used in combination with skeletal muscle relaxants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
